import React, { Component } from 'react';
import axios from 'axios';

export class Contact extends Component {
    constructor(props) {
        super(props);

        this.onChangeName = this.onChangeName.bind(this);
        this.onChangeEmail = this.onChangeEmail.bind(this);
        this.onChangePhoneNumber = this.onChangePhoneNumber.bind(this);
        this.onChangeMessage = this.onChangeMessage.bind(this);

        this.onSubmit = this.onSubmit.bind(this);

        this.state = {
            name: '',
            email: '',
            phonenumber: '',
            message: ''
        }
    }

    onChangeName(e) {
        this.setState({
            name: e.target.value
        })
    }

    onChangeEmail(e) {
        this.setState({
            email: e.target.value
        })
    }

    onChangePhoneNumber(e) {
        this.setState({
            phonenumber: e.target.value
        })
    }

    onChangeMessage(e) {
        this.setState({
            message: e.target.value
        })
    }

    onSubmit(e) {
        e.preventDefault();
        const contact = {
            name: this.state.name,
            email: this.state.email,
            phonenumber: this.state.phonenumber,
            message: this.state.message
        }
        console.log(contact);
        axios.post('http://localhost:5000/add', contact)
            .then(res => console.log(res.data));

        this.setState({
            name: '',
            email: '',
            phonenumber: '',
            message: ''
        })
    }

    render() {
        return (
            <section id="contact" className="container border-top border-dark">
                <div className="row contact">
                    <div className="col-md-8">
                        <h1>Contact Us Today!</h1>
                        <form onSubmit={this.onSubmit}>
                            <div className="form-group"> 
                                <label>Name: </label>
                                <input type="text"
                                    required
                                    className="form-control"
                                    value={this.state.name}
                                    onChange={this.onChangeName}
                                    />
                            </div>
                            <div className="form-group row"> 
                                <div className="col-md-6">
                                    <label>Email: </label>
                                    <input type="text"
                                        required
                                        className="form-control"
                                        value={this.state.email}
                                        onChange={this.onChangeEmail}
                                        />
                                </div>
                                <div className="col-md-6">
                                    <label>Phone: </label>
                                    <input type="text"
                                        required
                                        className="form-control"
                                        value={this.state.phonenumber}
                                        onChange={this.onChangePhoneNumber}
                                        />
                                </div>
                            </div>

                            <div className="form-group"> 
                                <label>Message: </label>
                                <textarea className="form-control"  cols="50" rows="15" value={this.state.message} onChange={this.onChangeMessage}></textarea>
                                {/* <input type="text"
                                    className="form-control"
                                    value={this.state.message}
                                    onChange={this.onChangeMessage}
                                    /> */}
                            </div>
                            <div className="form-group">
                                <input type="submit" value="Submit" className="btn btn-primary" />
                            </div>
                        </form>
                    </div>
                    <div className="col-md-4">
                        <h4> Address and Phone</h4>
                        <p> "Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum." </p>
                    </div>
                </div>

            </section>
        );
    }
}

export default Contact;
